using Syncytium.Common.Database.DSModel;using Syncytium.Common.Database.DSAnnotation;using Syncytium.Common.Database.DSAnnotation.DSConstraint;using Syncytium.Common.Database.DSAnnotation.DSControl;using Syncytium.Common.Database.DSAnnotation.DSFormat;using System;using System.ComponentModel.DataAnnotations;using System.ComponentModel.DataAnnotations.Schema;namespace Syncytium.Module.Sample.Models{    /// <summary>    /// Sample of table    /// </summary>    [Table("Sample")]    [DSRestricted(Area = DatabaseContext.AREA_NAME)]    public class SampleRecord : DSRecordWithCustomerId    {        /// <summary>        /// Name of the example        /// </summary>        [Required]        [DSString(Max = 32)]        [DSUnique(Fields = new string[] { "Name" })]        public String Name { get; set; } = String.Empty;        /// <summary>        /// Picture of the example        /// </summary>        [DSFile]        public byte[] Picture { get; set; } = null;        /// <summary>        /// Long description of the example        /// </summary>        [DSString(Max = 256)]        public String Description { get; set; } = null;        /// <summary>        /// Enable or disable the using of this example        /// </summary>        public bool Enable { get; set; } = true;        /// <summary>        /// Empty constructor        /// </summary>        public SampleRecord() : base () { }    }}
